import React, { Fragment } from "react";
import PropTypes from "prop-types";

const BreweryItem = ({
  brewery: {
    name,
    brewery_type,
    street,
    city,
    state,
    phone,
    website_url,
    tag_list
  }
}) => {
  const breweryType =
    brewery_type.slice(0, 1).toUpperCase() + brewery_type.slice(1);

  const phoneFormat = phone.replace(/(\d{3})(\d{3})(\d{4})/, "($1) $2-$3");

  tag_list = ["Patio"];
  return (
    <div className="card m-1">
      <h1
        className="card-header"
        style={{ fontFamily: "Merriweather", fontWeight: "normal" }}
      >
        {name}
      </h1>
      <div className="card-body">
        <h4>{street}</h4>
        <h5 className="card-title">
          {city}, {state}
        </h5>
        <h6 className="card-title">
          <strong>Phone: </strong>
          <a href={`tel://${phone}`}>{phoneFormat}</a>
        </h6>
        <h6 className="card-title">
          <strong>Type: </strong>
          {breweryType}
        </h6>
        <h6 className="card-title">
          {tag_list.length > 0 && <Fragment>Ammenities: {tag_list}</Fragment>}
        </h6>
        <h6>
          <strong>Website: </strong>
          <a href={website_url} target="_black">
            {website_url}
          </a>
        </h6>
        <h6>
          <a
            href={`https://www.google.com/maps/place/${street}`}
            target="_blank"
          >
            View Maps
          </a>
        </h6>
      </div>
    </div>
  );
};

BreweryItem.propTypes = {};

export default BreweryItem;
