import React from "react";
import Filters from "./Filters";

const Sidebar = () => {
  return (
    <div className="col-md-3 d-none d-md-block bg-light sidebar" style={styles}>
      <div className="sidebar-sticky">
        <Filters />
      </div>
    </div>
  );
};

const styles = {
  top: 56,
  bottom: 0,
  left: 0,
  right: 50,
  zIndex: 100,
  boxShadow: "inset -1px 0 0 rgba(0, 0, 0, .1)",
  position: "fixed",
  opacity: 0.5
};

export default Sidebar;
