import React, { useContext, useEffect } from "react";
import PropTypes from "prop-types";
import BreweryContext from "../../context/brewery/breweryContext";
import Spinner from "../layout/Spinner";

const Brewery = ({ match }) => {
  const breweryContext = useContext(BreweryContext);
  const { brewery, loading, getBrewery } = breweryContext;

  useEffect(() => {
    getBrewery(match.params.id);
    // eslint-disable-next-line
  }, []);

  const {
    brewery_type,
    city,
    state,
    country,
    name,
    phone,
    website_url
  } = brewery;

  if (loading) {
    return <Spinner />;
  } else {
    return (
      <div className="card m-5">
        <div className="text-center">
          <div className="card-header">
            <h1>{name}</h1>
          </div>
          <div className="card-body">
            <h5 className="card-title">good place for img</h5>
          </div>
        </div>
      </div>
    );
  }
};

Brewery.propTypes = {};

export default Brewery;
