import React, { Fragment, useContext } from "react";
import GoogleMap from "../layout/GoogleMap";
import Modal from "../layout/Modal";
import ModalContext from "../../context/modal/modalContext";

const BreweryItem = ({
  brewery: {
    name,
    brewery_type,
    street,
    city,
    state,
    phone,
    website_url,
    tag_list,
    longitude,
    latitude
  }
}) => {
  const breweryType =
    brewery_type.slice(0, 1).toUpperCase() + brewery_type.slice(1);

  const phoneFormat = phone.replace(/(\d{3})(\d{3})(\d{4})/, "($1) $2-$3");

  const modalContext = useContext(ModalContext);

  const { modal, showModal } = modalContext;

  const handleModalClick = () => {
    showModal();
  };

  return (
    <div className="card m-1 shadow rounded cardzoom">
      <h1
        className="card-header"
        style={{ fontFamily: "Merriweather", fontWeight: "normal" }}
      >
        {name}
      </h1>
      <div className="row no-gutters">
        <div className="col-md-8">
          <div className="card-body">
            <h4>{street}</h4>
            <h5 className="card-title">
              {city}, {state}
            </h5>
            <h6 className="card-title">
              <strong>Phone: </strong>
              <a href={`tel://${phone}`}>{phoneFormat}</a>
            </h6>
            <h6 className="card-title">
              <strong>Type: </strong>
              {breweryType}
            </h6>
            <h6 className="card-title">
              {tag_list.length > 0 && (
                <Fragment>
                  <strong> Ammenities: </strong>
                  {tag_list}
                </Fragment>
              )}
            </h6>
            <h6>
              <strong>Website: </strong>
              <a href={website_url} target="_black">
                {website_url}
              </a>
            </h6>
            <h6>
              {/* <a
                href={`https://www.google.com/maps/place/${street},${city},${state}`}
                target="_blank"
                rel="noopener noreferrer"
              >
                View Maps
              </a> */}
            </h6>
            <h6>
              <button
                type="button"
                className="btn btn-primary"
                data-toggle="modal"
                data-target="#googlemapsmodal"
                onClick={handleModalClick}
              >
                View Maps
              </button>
              {modal && <Modal id="googleMapsModal" jsx={GoogleMap} />}
            </h6>
          </div>
        </div>
        {/* <div className="col-md-4">
          <GoogleMap name={name} longitude={longitude} latitude={latitude} />
        </div> */}
      </div>
    </div>
  );
};

BreweryItem.propTypes = {};

export default BreweryItem;
