import React from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

const BreweryItem = ({
  breweryI: { name, brewery_type, street, city, state, phone, website_url }
}) => {
  const breweryType =
    brewery_type.slice(0, 1).toUpperCase() + brewery_type.slice(1);

  const phoneFormat = phone.replace(/(\d{3})(\d{3})(\d{4})/, "($1) $2-$3");

  return (
    <div className="card">
      <h1 className="card-header">{name}</h1>
      <div className="card-body">
        <h4>{street}</h4>
        <h5 className="card-title">
          {city}, {state}
        </h5>
        <h6 className="card-title">
          <strong>Type: </strong>
          {breweryType}
        </h6>
        <h6 className="card-title">
          <strong>Phone: </strong>
          {phoneFormat}
        </h6>
        <h6>
          <strong>Website: </strong>
          <a href={website_url} target="_black">
            {website_url}
          </a>
        </h6>
      </div>
    </div>
  );
};

BreweryItem.propTypes = {};

export default BreweryItem;
