import React, { useReducer, useContext } from "react";
import axios from "axios";
import BreweryContext from "./breweryContext";
import BreweryReducer from "./breweryReducer";
import AlertContext from "../../context/alert/alertContext";
import {
  SEARCH_BREWERIES,
  AUTO_COMPLETE,
  GET_BREWERY,
  SET_LOADING,
  CLEAR_BREWERIES
} from "../types";

import BreweryURLBuilder from "../../utils/BreweryURLBuilder";

const BreweryState = props => {
  const initialState = {
    breweries: [],
    suggestions: [],
    brewery: {},
    loading: false
  };

  const [state, dispatch] = useReducer(BreweryReducer, initialState);

  const alertContext = useContext(AlertContext);

  const searchBreweries = async url => {
    setLoading();
    const res = await axios.get(url);
    if (res.data.length === 0) {
      alertContext.setAlert(
        "Sorry, we could not find breweries for what you're looking for. Please try another search term.",
        "warning"
      );
    }
    dispatch({
      type: SEARCH_BREWERIES,
      payload: res.data
    });
  };

  const searchAutoComplete = async query => {
    const res = await axios.get(
      `https://api.openbrewerydb.org/breweries/autocomplete?query=${query}`
    );

    dispatch({
      type: AUTO_COMPLETE,
      payload: res.data
    });
  };

  const getUserLocation = async (lat, long) => {
    setLoading();
    const res = await axios.get(
      `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${long}&key=AIzaSyDszS9vH_VpB6mumst6qwxOXfOAIqpcXpg`
    );

    let city = res.data.results[0].address_components[3].long_name
      .toLowerCase()
      .replace("the ", "");
    let state = res.data.results[0].address_components[5].long_name
      .toLowerCase()
      .replace(" ", "_");

    const url = new BreweryURLBuilder().setCity(city).setState(state);

    searchBreweries(url.getUrl());
  };

  const getBrewery = async id => {
    setLoading();
    const res = await axios.get(
      `https://api.openbrewerydb.org/breweries/${id}`
    );
    dispatch({
      type: GET_BREWERY,
      payload: res.data
    });
  };

  const clearBreweries = () => {
    dispatch({ type: CLEAR_BREWERIES });
  };

  const setLoading = () => dispatch({ type: SET_LOADING });

  return (
    <BreweryContext.Provider
      value={{
        breweries: state.breweries,
        brewery: state.brewery,
        loading: state.loading,
        searchBreweries,
        searchAutoComplete,
        getUserLocation,
        getBrewery,
        clearBreweries
      }}
    >
      {props.children}
    </BreweryContext.Provider>
  );
};

export default BreweryState;
