import React, { useContext, useEffect } from "react";
import PropTypes from "prop-types";
import BreweryContext from "../../context/brewery/breweryContext";
import Spinner from "../layout/Spinner";

const Brewery = ({ match }) => {
  const breweryContext = useContext(BreweryContext);
  const { brewery, loading, getBrewery } = breweryContext;

  useEffect(() => {
    getBrewery(match.params.id);
    // eslint-disable-next-line
  }, []);

  const {
    brewery_type,
    city,
    state,
    country,
    name,
    phone,
    website_url
  } = brewery;

  if (brewery_type) {
    const breweryType =
      brewery_type.slice(0, 1).toUpperCase() + brewery_type.slice(1);
  }

  if (loading) {
    return <Spinner />;
  } else {
    return (
      <div className="card m-5">
        <div className="text-center">
          <div className="card-header">
            <h1>
              <a href={website_url} target="_blank">
                {name}
              </a>
            </h1>
          </div>
          <div className="card-body">
            <h5 className="card-title">good place for img</h5>
          </div>
          <div className="card-footer text-muted">
            <p>Brewery Type: {breweryType || ""}</p>
            <p>
              {city}, {state}
            </p>
            <p>{country}</p>
          </div>
        </div>
      </div>
    );
  }
};

Brewery.propTypes = {};

export default Brewery;
