import React from "react";
import PropTypes from "prop-types";

const BreweryItem = ({ brewery: { name, brewery_type } }) => {
  const breweryType =
    brewery_type.slice(0, 1).toUpperCase() + brewery_type.slice(1);

  return (
    <div className="card text-center">
      <h5 className="card-header">{name}</h5>
      <div className="card-body">
        <h6 className="card-title">{breweryType}</h6>

        <input type="button" value="More Info" className="btn btn-info" />
      </div>
    </div>
  );
};

BreweryItem.propTypes = {};

export default BreweryItem;
