import { SHOW_MODAL, HIDE_MODAL } from "../types";

export default (state, action) => {
  switch (action.type) {
    case SHOW_MODAL:
      return action.payload;
    case HIDE_MODAL:
      return null;
    default:
      return state;
  }
};
