import React from "react";

const Search = () => {
  return (
    <div className="m-2">
      <form className="form">
        <input
          type="text"
          name="text"
          value=""
          class="form-control"
          placeholder="Search Breweries..."
        />
        <input
          type="submit"
          value="Search"
          className="btn btn-primary btn-block m-2"
        />
      </form>
    </div>
  );
};

export default Search;
