import React, { useState, useContext } from "react";
import BreweryContext from "../../context/brewery/breweryContext";
import AlertContext from "../../context/alert/alertContext";

const Search = () => {
  const breweryContext = useContext(BreweryContext);
  const alertContext = useContext(AlertContext);

  const [text, setText] = useState("");

  const { searchBreweries, clearBreweries } = breweryContext;

  const onChange = e => setText(e.target.value);

  const onSubmit = e => {
    e.preventDefault();
    if (text === "") {
      alertContext.setAlert("Please enter something", "warning");
    } else {
      searchBreweries(text);
      setText("");
    }
  };

  const clearAll = () => {
    clearBreweries();
  };

  return (
    <div className="m-2">
      <form className="form" onSubmit={onSubmit}>
        <input
          type="text"
          name="text"
          value={text}
          className="form-control mb-2"
          placeholder="Search Breweries..."
          onChange={onChange}
        />
        <div className="row">
          <input
            type="submit"
            value="Search"
            className="btn btn-primary btn-block"
          />
          <input
            type="button"
            value="ClearAll"
            className=""
            onClick={clearAll}
          />
        </div>
      </form>
    </div>
  );
};

export default Search;
